// POST /contributions

import { Prisma } from "@prisma/client";
import { RequestHandler } from "express";
import { Services } from "../types";
import { AddSignatureRequest, AddSignatureResponse } from "common/server-api";

export function addSignature({ prisma }: Services): RequestHandler {
  return async (req, res) => {
    const { walletId } = req.body as AddSignatureRequest;

    // TODO: validate request, maybe use autogenerated zod

    try {
      const result = await prisma.user.create({
        data: {
          id: walletId,
        },
      });

      res.json(result.id as AddSignatureResponse);
    } catch (err) {
      console.log(err);
      if (err instanceof Prisma.PrismaClientValidationError) {
        res.status(400).json({ error: "Received invalid data." });
        return;
      }
      res.status(400).json({ error: err.message });
    }
  };
}
